var typeMatchers = require('project/type-matchers')

var have = require('have2').with(typeMatchers).strict

var assert = have.assert
var funcName

have.assert(function(cond, msg) {
  assert(cond, funcName ? funcName + ': ' + msg : msg);
});

module.exports = (function(have_) {
  return function(args, schema) {
    funcName = args.callee ? args.callee.name : null
    have_(args, schema)
    funcName = null
  };
})(have)
