var Jsonix = require('../../../../vendor/jsonix').Jsonix;

module.exports = {
    createContext: function (mapping, opt) {

        if (opt && opt.discardTimezone) {
            require('./fixDateTime')(Jsonix);
        }

        return new Jsonix.Context(mapping);
    }
};